SET SEARCH_PATH = unions;

DROP SCHEMA IF EXISTS unions CASCADE;

CREATE SCHEMA unions;

SET SEARCH_PATH = unions;

/* USER TABLES */
CREATE TABLE "user" (
  user_id        SERIAL      NOT NULL PRIMARY KEY,
  email_address  VARCHAR(80) NOT NULL UNIQUE,
  sso            VARCHAR(30),
  personal_info  TEXT, /*First Name, Last Name, Preferred Name, Preferred Email Address*/
  last_login     TIMESTAMP,
  create_time    TIMESTAMP,
  create_user_id INTEGER,
  update_time    TIMESTAMP,
  update_user_id INTEGER
);

CREATE TABLE user_auth (
  user_id       INTEGER PRIMARY KEY REFERENCES "user" ON DELETE CASCADE ON UPDATE CASCADE,
  password_hash CHAR(40),
  salt          CHAR(40)
);
/* END USER TABLES*/

/* POLICY TABLES */
CREATE TABLE category (
  category_id    SERIAL      NOT NULL PRIMARY KEY,
  name           VARCHAR(60) NOT NULL,
  create_time    TIMESTAMP,
  create_user_id INTEGER,
  update_time    TIMESTAMP,
  update_user_id INTEGER
);

CREATE TABLE policy (
  policy_id      SERIAL  NOT NULL PRIMARY KEY,
  title          TEXT    NOT NULL,
  text           TEXT    NOT NULL,
  category_id    INTEGER NOT NULL REFERENCES category ON DELETE CASCADE ON UPDATE CASCADE,
  active         BOOL DEFAULT TRUE,
  create_time    TIMESTAMP,
  create_user_id INTEGER,
  update_time    TIMESTAMP,
  update_user_id INTEGER
);

CREATE TABLE tag (
  tag_id SERIAL      NOT NULL PRIMARY KEY,
  tag    VARCHAR(25) NOT NULL
);

CREATE TABLE tagged (
  policy_id INTEGER NOT NULL REFERENCES policy ON DELETE CASCADE ON UPDATE CASCADE,
  tag_id    INTEGER NOT NULL REFERENCES tag ON DELETE CASCADE ON UPDATE CASCADE,
  PRIMARY KEY (policy_id, tag_id)
);
/* END POLICY TABLES */

/* RESERVATION TABLES */
CREATE TABLE building (
  building_id    SERIAL PRIMARY KEY NOT NULL,
  name           VARCHAR(60)        NOT NULL,
/*coordinate*/
  address        VARCHAR(100),
  open_time      TIME,
  close_time     TIME,
  create_time    TIME,
  create_user_id INTEGER,
  update_time    TIMESTAMP,
  update_user_id INTEGER
);

CREATE TABLE floor (
  floor_id       SERIAL  NOT NULL PRIMARY KEY,
  name           VARCHAR(40),
  building_id    INTEGER NOT NULL REFERENCES building,
  create_time    TIMESTAMP,
  create_user_id INTEGER,
  update_time    TIMESTAMP,
  update_user_id INTEGER
);

CREATE TABLE event_space (
  event_space_id SERIAL      NOT NULL PRIMARY KEY,
  name           VARCHAR(45) NOT NULL,
  floor_id       INTEGER     NOT NULL REFERENCES floor,
  capacity       INTEGER     NOT NULL,
/*reserved       BOOLEAN DEFAULT FALSE -- This should be checked by start and end time and not stored in the database */
  image_path     VARCHAR(100),
  create_time    TIMESTAMP,
  create_user_id INTEGER,
  update_time    TIMESTAMP,
  update_user_id INTEGER
);

CREATE TABLE reservation (
  reservation_id INTEGER   NOT NULL PRIMARY KEY,
  event_space_id INTEGER   NOT NULL REFERENCES event_space,
  user_id        INTEGER   NOT NULL REFERENCES "user",
  start_time     TIMESTAMP NOT NULL, --want to make unique
  end_time       TIMESTAMP NOT NULL, --want to make unique
  create_time    TIMESTAMP,
  create_user_id INTEGER,
  update_time    TIMESTAMP,
  update_user_id INTEGER
);

CREATE TABLE feature (
  feature_id     SERIAL       NOT NULL,
  floor_id       INTEGER      NOT NULL REFERENCES floor,
  building_id    INTEGER      NOT NULL REFERENCES building,
  name           VARCHAR(100) NOT NULL,
  url            VARCHAR(150),
  event_space_id INTEGER, /* NULL is allowed so no foreign key */
  create_time    TIMESTAMP,
  create_user_id INTEGER,
  update_time    TIMESTAMP,
  update_user_id INTEGER
);

CREATE TABLE attraction (
  attraction_id    SERIAL PRIMARY KEY NOT NULL,
  name             VARCHAR(100)       NOT NULL,
  image_path       VARCHAR(100),
  lim_id           INTEGER,
  information_url  VARCHAR(100),
  url_display_name VARCHAR(60),
  create_time      TIMESTAMP,
  create_user_id   INTEGER,
  update_time      TIMESTAMP,
  update_user_id   INTEGER,
  building_id      INTEGER            NOT NULL REFERENCES building
);
/* END RESERVATION TABLES */

/* LOST & FOUND TABLES */


CREATE TABLE item_type (
  item_type_id   SERIAL PRIMARY KEY,
  name           VARCHAR(45) NOT NULL,
  create_time    TIMESTAMP,
  create_user_id INTEGER,
  update_time    TIMESTAMP,
  update_user_id INTEGER
);
CREATE TABLE item_status (
  item_status_id SERIAL PRIMARY KEY,
  description    VARCHAR(45) NOT NULL
);
CREATE TABLE item (
  item_id          SERIAL PRIMARY KEY,
  location         VARCHAR(255) NOT NULL,
  description      VARCHAR(255) NOT NULL,
  found_user		VARCHAR(45) NOT NULL,
  found_date       DATE         NOT NULL,
  item_type_id     INTEGER      NOT NULL REFERENCES item_type,
  status         INTEGER     NOT NULL DEFAULT 0 REFERENCES item_status, /* -1 = DECLINED, 0 = PENDING, 1 = APPROVED */
  create_time      TIMESTAMP,
  create_user_id   INTEGER,
  update_time      TIMESTAMP,
  update_user_id   INTEGER
);
CREATE TABLE item_claim (
	id SERIAL PRIMARY KEY,
  item_id    INTEGER NOT NULL REFERENCES item,
  first_name VARCHAR(100),
  last_name VARCHAR(100),
  email VARCHAR(100)
);


/* END LOST & FOUND TABLES */

